import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import axios from "axios";
import {Posting} from "../../types";

const BoardDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>(); // URL 파라미터에서 id를 가져옵니다.
    const [posting, setPosting] = useState<Posting>();

    useEffect(() => {
        axios.get(`/boarddetail/${id}`)

            .then(response => setPosting(response.data))
            .catch(error => {
                console.error('게시물을 가져오는 데 실패했습니다.', error);
            });
    }, [id]);

    return (
        <main>
            <h2>게시물 상세 페이지</h2>
            {posting ? (
                <div>
                    <h3>글 번호: {posting.id}</h3>
                    <h3>작성자: {posting.name}</h3>
                    <h3>제목: {posting.title}</h3>
                    <h3>작성일: {posting.createdate}</h3>
                    <h3>조회수: {posting.views}</h3>
                    <p>내용: {posting.content}</p>
                </div>
            ) : (
                <p>게시물을 불러오는 중입니다...</p>
            )}
        </main>
    );
};

export default BoardDetail;
