import React from 'react';
import { Posting } from "../../types";
import "../../styles/communityStyles/boardLayout.css"

interface BoardLayOutProps {
    postings: Posting[];
}

const BoardLayOut: React.FC<BoardLayOutProps> = ({ postings }) => {

    const handleTitleClick = (id) => {
        fetch(`/api/postings/${id}`)
            .then((response) => response.json())
            .then((data) => {
                console.log(data); // 예시: 서버로부터 받은 데이터 출력
                // 원하는 동작 수행
            })
            .catch((error) => {
                console.error('Error:', error); // 예시: 에러 처리
            });
    };

    return (
        <main className={"boardLayOut"}>
            <section className={"topSide"}>

            </section>

            <table>
                <colgroup>
                    <col style={{ width: '10%' }} />
                    <col style={{ width: '15%' }} />
                    <col style={{ width: '45%' }} />
                    <col style={{ width: '20%' }} />
                    <col style={{ width: '10%' }} />
                </colgroup>
                <thead>
                    <tr>
                        <th>글 번호</th>
                        <th>작성자</th>
                        <th>제목</th>
                        <th>작성일</th>
                        <th>조회수</th>
                    </tr>
                </thead>
                <tbody>
                    {postings.map((posting) => (
                        <tr key={posting.id}>
                            <td>{posting.id}</td>
                            <td>{posting.name}</td>
                            <span onClick={() => handleTitleClick(posting.id)}>{posting.title}</span>
                            <td>{posting.createdate}</td>
                            <td>{posting.views}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <section className={"bottomSide"}>

            </section>
        </main>
    );
};

export default BoardLayOut;
